import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './tmdb.css';

const RecentMovies = () => {
    const [movies, setMovies] = useState([]);
    const [expandedMovieId, setExpandedMovieId] = useState(null);
    const [hideHomepageMovies, setHideHomepageMovies] = useState(false);
  
    useEffect(() => {
      const fetchMovies = async () => {
        try {
          const response = await axios.get('https://api.themoviedb.org/3/trending/tv/week', {
            params: {
              api_key: 'a6c3b3938c76f38f3289b3291fd0629f',
            },
          });
  
          setMovies(response.data.results);
        } catch (error) {
          console.error('Error fetching TV shows:', error);
        }
      };
  
      fetchMovies();
    }, []);
  
    const toggleExpand = movieId => {
      if (expandedMovieId === movieId) {
        setExpandedMovieId(null);
      } else {
        setExpandedMovieId(movieId);
      }
    };
  
    const renderOverviewText = (overview, movieId) => {
      if (overview.length <= 200 || expandedMovieId === movieId) {
        return overview;
      }
  
      return overview.slice(0, 200) + '...';
    };
  
    return (
      <div className="recent-movies-container" id="homepage-movies" style={{ display: hideHomepageMovies ? 'none' : 'block' }}>
        <h2>Recent TV Shows</h2>
        <div className="movies-grid">
          {movies.map(movie => (
            <div key={movie.id} className="movie-card">
              <h3>{movie.name}</h3>
              <img
                src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                alt={movie.name}
                className="movie-poster"
              />
              <p>Rating: {movie.vote_average}</p>
              <p>{movie.first_air_date}</p>
              <p>{renderOverviewText(movie.overview, movie.id)}</p>
              {movie.overview.length > 200 && (
                <button className="toggle-button" onClick={() => toggleExpand(movie.id)}>
                  {expandedMovieId === movie.id ? 'Read Less' : 'Read More'}
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default RecentMovies;
