import React from 'react';

const PageHeader = () => {
  const handleClick = () => {
    window.location.reload();
  };

  return (
    <header style={{ width: '100%' }}>
      <img
        src="/path/to/logo.png"
        alt="Logo"
        style={{ cursor: 'pointer' }}
        onClick={handleClick}
      />
    </header>
  );
};

export default PageHeader;
